SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_execucao_cirurgica_pck.registrar_partic_bio ( nr_seq_exec_cirurg_biometria_p pls_exec_cirurg_biometria.nr_sequencia%type, nr_seq_exec_cirurg_bio_part_p pls_exec_cirurg_bio_partic.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Vincular as participacoes do medico na biometria capturada.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_medico_w				pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_exec_cirurgica_w			pls_execucao_cirurgica.nr_sequencia%type;
nr_seq_exec_cirurg_bio_part_w		pls_exec_cirurg_bio_partic.nr_sequencia%type;	


BEGIN

begin
	select	cd_medico,
		nr_seq_exec_cirurgica
	into STRICT	cd_medico_w,
		nr_seq_exec_cirurgica_w
	from	pls_exec_cirurg_biometria
	where	nr_sequencia	= nr_seq_exec_cirurg_biometria_p;
exception
when others then
	cd_medico_w	:= null;
end;

if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then
	select	a.nr_sequencia
	into STRICT	nr_seq_exec_cirurg_bio_part_w
	from	pls_exec_cirurg_bio_partic a,
		pls_exec_cirurgica_proc b
	where	a.nr_seq_exec_cirurg_proc	= b.nr_sequencia
	and	a.nr_sequencia			= nr_seq_exec_cirurg_bio_part_p
	and	coalesce(a.nr_seq_exec_cirurg_bio::text, '') = ''
	and	a.ie_status			= 'A'
	and (pls_execucao_cirurgica_pck.obter_se_medico_partic(nr_seq_exec_cirurgica_w, b.nr_sequencia, cd_medico_w) = 'S');
	
	if (nr_seq_exec_cirurg_bio_part_w IS NOT NULL AND nr_seq_exec_cirurg_bio_part_w::text <> '') then
		update	pls_exec_cirurg_bio_partic
		set	nr_seq_exec_cirurg_bio	= nr_seq_exec_cirurg_biometria_p,
			ie_status		= 'E',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_exec_cirurg_bio_part_w;
		
		update	pls_exec_cirurgica_guia
		set	ie_status		= 'E',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()	
		where	nr_seq_exec_cirurgica	= nr_seq_exec_cirurgica_w
		and	ie_status		= 'A';
	else
		--Participacao nao liberada para o profissional.
		CALL pls_execucao_cirurgica_pck.gerar_log_exec_cirurgica(nr_seq_exec_cirurgica_w, wheb_mensagem_pck.get_texto(1146265,'NR_SEQ_PARTIC=' || nr_seq_exec_cirurg_bio_part_w), cd_estabelecimento_p, nm_usuario_p);
	end if;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_execucao_cirurgica_pck.registrar_partic_bio ( nr_seq_exec_cirurg_biometria_p pls_exec_cirurg_biometria.nr_sequencia%type, nr_seq_exec_cirurg_bio_part_p pls_exec_cirurg_bio_partic.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
