CREATE OR REPLACE FUNCTION pls_execucao_cirurgica_pck.valida_se_permite_checkin ( nr_seq_exec_cirurgica_p pls_execucao_cirurgica.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Validar a regra de tolrencia de checkin. 
Retorna 'S' se a data/hora atual estiver dentro do limite permitido para novo checkin.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(1)	:= 'S';
qt_minutos_restricao_w		pls_exec_cirurgica_regras.qt_minutos_restricao%type;
dt_primeiro_check_in_w		timestamp;
dt_limite_checkin_w		timestamp;


BEGIN

select	max(qt_minutos_restricao)
into STRICT	qt_minutos_restricao_w
from	pls_exec_cirurgica_regras
where	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_minutos_restricao_w IS NOT NULL AND qt_minutos_restricao_w::text <> '') then
	select	min(dt_inicio_check_in)
	into STRICT	dt_primeiro_check_in_w
	from	pls_exec_cirurgica_guia
	where	nr_seq_exec_cirurgica	= nr_seq_exec_cirurgica_p
	and	ie_status		<> 'F';
	
	if (dt_primeiro_check_in_w IS NOT NULL AND dt_primeiro_check_in_w::text <> '') then	
		dt_limite_checkin_w	:= (dt_primeiro_check_in_w + qt_minutos_restricao_w / 1440);
		
		if (clock_timestamp() > dt_limite_checkin_w) then
			ds_retorno_w	:= 'N';
		end if;
	end if;
end if;
			
return	ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
