CREATE OR REPLACE PROCEDURE pls_faturamento_pck.carregar_evento_mat_fat ( tb_nr_seq_item_p pls_util_cta_pck.t_number_table, tb_nr_seq_evento_p pls_util_cta_pck.t_number_table, tb_nr_seq_regra_fat_p pls_util_cta_pck.t_number_table, tb_nr_agrupamento_p pls_util_cta_pck.t_number_table, tb_vl_item_p pls_util_cta_pck.t_number_table, tb_nr_seq_pos_item_fat_p pls_util_cta_pck.t_number_table, tb_nr_seq_conta_pos_item_p pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type ) AS $body$
BEGIN
forall	i in tb_nr_seq_item_p.first..tb_nr_seq_item_p.last
	update	w_pls_lote_fat_mat
	set	vl_item			= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE vl_item END ,
		nr_seq_evento_fat	= tb_nr_seq_evento_p(i),
		nr_seq_regra_fat	= tb_nr_seq_regra_fat_p(i),
		nr_agrupamento		= tb_nr_agrupamento_p(i),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= tb_nr_seq_item_p(i);
	commit;

forall	i in tb_nr_seq_item_p.first..tb_nr_seq_item_p.last
	update	pls_conta_pos_mat_fat
	set	vl_administracao	= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE vl_administracao END ,
		vl_lib_taxa_material	= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE vl_lib_taxa_material END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= tb_nr_seq_pos_item_fat_p(i);
	commit;
	
forall	i in tb_nr_seq_item_p.first..tb_nr_seq_item_p.last
	update	pls_conta_pos_mat
	set	tx_administracao	= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE tx_administracao END ,
		vl_lib_taxa_material	= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE vl_lib_taxa_material END ,
		vl_administracao	= CASE WHEN tb_vl_item_p(i)=0 THEN  0  ELSE vl_administracao END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia 		= tb_nr_seq_conta_pos_item_p(i);
	commit;

END;

$body$
