-- Mesmo tratamento da rotina PLS_CONSISTIR_REGRA_ATEND_CART
CREATE OR REPLACE PROCEDURE pls_faturamento_pck.consistir_regra_atend_cart ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_regra_atend_cart_p pls_oc_atend_cart_regra.nr_seq_regra%type, cd_usuario_plano_p pls_segurado_carteira.cd_usuario_plano%type, ie_glosou_p INOUT text) AS $body$
DECLARE


ie_glosou_w		varchar(1) := 'N';

C01 CURSOR(	nr_seq_regra_atend_cart_pc pls_oc_atend_cart_regra.nr_seq_regra%type ) FOR
	SELECT	a.nr_sequencia nr_seq_oc_atend_cart_regra,
		substr(cd_usuario_plano_p,a.qt_pos_inicio,(a.qt_pos_final - a.qt_pos_inicio)+1) cd_faixa_carteira
	from	pls_oc_atend_cart_regra	a
	where	a.nr_seq_regra	= nr_seq_regra_atend_cart_pc;
	
C02 CURSOR(	nr_seq_oc_atend_cart_regra_pc pls_oc_atend_cart_regra.nr_sequencia%type) FOR
	SELECT	a.cd_carteira
	from	pls_oc_atend_cart_cod	a
	where	a.nr_seq_regra_cart	= nr_seq_oc_atend_cart_regra_pc;
BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') and (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') then
	
	for r_c01_w in c01( nr_seq_regra_atend_cart_p ) loop
	
		for r_c02_w in c02( r_c01_w.nr_seq_oc_atend_cart_regra ) loop		
			if (r_c02_w.cd_carteira = r_c01_w.cd_faixa_carteira) then
				ie_glosou_w	:= 'S';
				exit;
			end if;
		end loop;
		
		if (ie_glosou_w = 'S') then
			exit;
		end if;
	end loop;
end if;
ie_glosou_p	:= ie_glosou_w;

END;

$body$
