CREATE OR REPLACE PROCEDURE pls_faturamento_pck.inicializa_geracao_lote ( nr_seq_lote_p pls_lote_faturamento.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


-- Finalidade:	Marca o lote como inicializado a geracao
ds_sql_w	varchar(4000);
nm_id_sid_w	pls_lote_faturamento.nm_id_sid%type;
nm_id_serial_w	pls_lote_faturamento.nm_id_serial%type;


BEGIN

-- levanda a identificacao da conexao
ds_sql_w := 	'select	sid, ' || pls_util_pck.enter_w ||
		'	serial# ' || pls_util_pck.enter_w ||
		'	from	v$session ' || pls_util_pck.enter_w ||
		'where	audsid = userenv(''SESSIONID'') ';
		
EXECUTE ds_sql_w into STRICT nm_id_sid_w, nm_id_serial_w;

update	pls_lote_faturamento
set	dt_inicio_ger_lote	= clock_timestamp(),
	nm_usuario_ger_lote	= nm_usuario_p,
	nm_id_sid		= nm_id_sid_w,
	nm_id_serial		= nm_id_serial_w,
	ie_status		= 2, --Em processamento
	dt_fim_ger_lote		 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_lote_p;

commit;

END;

$body$
