-- Pegar o ato cooperado do item faturado
CREATE OR REPLACE FUNCTION pls_faturamento_pck.obter_ato_cooperado_fat ( nr_sequencia_p pls_conta_medica_resumo.nr_sequencia%type, nr_seq_conta_p pls_conta_medica_resumo.nr_seq_conta%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

							
ie_ato_cooperado_w	pls_conta_medica_resumo.ie_ato_cooperado%type;


BEGIN
-- Obter da conta medica resumo
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	
	-- Procedimento
	if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
		select	max(ie_ato_cooperado)
		into STRICT	ie_ato_cooperado_w
		from	pls_conta_medica_resumo
		where	nr_sequencia		= nr_sequencia_p
		and	nr_seq_conta		= nr_seq_conta_p
		and	nr_seq_conta_proc	= nr_seq_conta_proc_p;
	end if;
	
	-- Material
	if (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
		select	max(ie_ato_cooperado)
		into STRICT	ie_ato_cooperado_w
		from	pls_conta_medica_resumo
		where	nr_sequencia		= nr_sequencia_p
		and	nr_seq_conta		= nr_seq_conta_p
		and	nr_seq_conta_mat	= nr_seq_conta_mat_p;
	end if;
end if;

if (coalesce(ie_ato_cooperado_w::text, '') = '') then
	-- Procedimento
	if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
		select	max(ie_ato_cooperado)
		into STRICT	ie_ato_cooperado_w
		from	pls_conta_proc
		where	nr_sequencia	= nr_seq_conta_proc_p;
	end if;

	-- Material
	if (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
		select	max(ie_ato_cooperado)
		into STRICT	ie_ato_cooperado_w
		from	pls_conta_mat
		where	nr_sequencia	= nr_seq_conta_mat_p;		
	end if;
end if;
				
return ie_ato_cooperado_w;

END;

