CREATE OR REPLACE PROCEDURE pls_faturamento_pck.preparar_base () AS $body$
DECLARE


-- Objetivo e prepara a base para geracao do lote de faturamento
BEGIN
-- Caso o tipo de apresentaca do protocolo for nulo, ele e de apresentacao
update	pls_protocolo_conta
set	ie_apresentacao = 'A'
where	coalesce(ie_apresentacao::text, '') = '';

commit;

END;

