SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_faturamento_pck.remover_contas_sem_evento ( nr_seq_lote_p pls_lote_faturamento.nr_sequencia%type) AS $body$
DECLARE


tb_rowid_proc_w	pls_util_cta_pck.t_rowid;
tb_rowid_mat_w	pls_util_cta_pck.t_rowid;

-- Obter contas sem evento (procedimento)
C01 CURSOR FOR
	SELECT	oid
	from	w_pls_lote_fat_proc
	where	nr_seq_lote_fat	= nr_seq_lote_p
	and	coalesce(nr_seq_evento_fat,-1) = -1;

-- Obter contas sem evento (procedimento)
C02 CURSOR FOR	
	SELECT	oid
	from	w_pls_lote_fat_mat
	where	nr_seq_lote_fat	= nr_seq_lote_p
	and	coalesce(nr_seq_evento_fat,-1) = -1;


BEGIN

open C01;
loop
	fetch C01 bulk collect into tb_rowid_proc_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_rowid_proc_w.count = 0;
	
	-- Remover os procedimentos de contas que possuem itens sem evento
	forall	i in tb_rowid_proc_w.first..tb_rowid_proc_w.last
		delete	FROM w_pls_lote_fat_proc
		where	rowid	= tb_rowid_proc_w(i);
	commit;
	
	tb_rowid_proc_w.delete;
	
end loop;

if (C01%isOpen) then

	close C01;
end if;

open C02;
loop
	fetch C02 bulk collect into tb_rowid_mat_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_rowid_mat_w.count = 0;
	

	-- Remover os materiais de contas que nao foram faturas
	forall	i in tb_rowid_mat_w.first..tb_rowid_mat_w.last
		delete	FROM w_pls_lote_fat_mat
		where	rowid	= tb_rowid_mat_w(i);
	commit;
	
	tb_rowid_mat_w.delete;
end loop;

if (C02%isOpen) then

	close C02;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
