CREATE OR REPLACE FUNCTION pls_faturamento_pck.retorna_lote_sem_session ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


-- Finalidade:	Busca por lotes de faturamento, que foram iniciada a sua geracao, mas por algum 
--	motivo a session da geracao foi interrompida, e os tratamentos de exception nao foram executados.
ds_retorno_w	pls_lote_faturamento.nr_sequencia%type;

BEGIN

-- carrega o sequencial mais recente do lote que foi considerado invalido
select	max(a.nr_sequencia)
into STRICT	ds_retorno_w
from	pls_lote_faturamento	a
where	(a.dt_inicio_ger_lote IS NOT NULL AND a.dt_inicio_ger_lote::text <> '')
and	coalesce(a.dt_fim_ger_lote::text, '') = ''
and	(a.nm_id_serial IS NOT NULL AND a.nm_id_serial::text <> '')
and	(a.nm_id_sid IS NOT NULL AND a.nm_id_sid::text <> '')
and	a.ie_status		= 2 -- Em processamento
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	not exists (	SELECT	1
					from	gv$session	x
					where	x.serial#	= a.nm_id_serial
					and	x.sid		= nm_id_sid);
return ds_retorno_w;
					
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
