CREATE OR REPLACE FUNCTION pls_filtro_regra_event_cta_pck.obter_alias_tabela (ds_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_alias_w	varchar(10);


BEGIN

-- retorna o alias que sera utilizado no select de acordo com a tabela

case(ds_tabela_p)
	when 'protocolo' then
		ds_alias_w := 'prot';
		
	when 'conta' then
		ds_alias_w := 'conta';
		
	when 'procedimento' then
		ds_alias_w := 'proc';
		
	when 'material' then
		ds_alias_w := 'mat';
		
	when 'selecao' then
		ds_alias_w := 'sele';
		
	when 'resumo' then
		ds_alias_w := 'res';
	
	when 'prestador' then
		ds_alias_w := 'pre';
	else
		ds_alias_w := null;
end case;

return ds_alias_w;

END;

$body$
