CREATE OR REPLACE FUNCTION pls_filtro_regra_event_cta_pck.obter_restricao_material ( nr_seq_material_p pls_pp_cta_filtro_mat.nr_seq_material%type, nr_seq_estrut_mat_p pls_pp_cta_filtro_mat.nr_seq_estrut_mat%type, ie_tipo_despesa_p pls_pp_cta_filtro_mat.ie_tipo_despesa_mat%type, ds_campos_p out text, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

					
ds_restricao_mat_w	varchar(2500);
