BEGIN

ds_campos_w := null;
ds_alias_mat_w := pls_filtro_regra_event_cta_pck.obter_alias_tabela('material');

-- sequencia do material

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	ds_restricao_mat_w := ds_restricao_mat_w || '	and ' || ds_alias_mat_w || '.nr_seq_material = :nr_seq_material_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(':nr_seq_material_pc', nr_seq_material_p, valor_bind_p);
