CREATE OR REPLACE PROCEDURE pls_filtro_regra_event_rgl_pck.atualiza_sel_excecao_end ( nr_id_transacao_p pls_pp_cta_rec_selecao.nr_id_transacao%type, ie_processou_filtro_p boolean, ie_excecao_p pls_pp_cta_filtro.ie_excecao%type) AS $body$
BEGIN

-- so atualiza se for excecao e se tiver processado algum filtro

if (ie_excecao_p = 'S') and (ie_processou_filtro_p = true) then
	-- so atualiza se valido.

	if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then
		-- descarta o que e excecao

		update	pls_pp_cta_rec_selecao
		set	ie_valido = 'N',
			ie_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao = 'S';
		commit;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
