CREATE OR REPLACE PROCEDURE pls_filtro_regra_event_rgl_pck.gerencia_selecao_filtro ( nr_id_transacao_p pls_pp_cta_rec_selecao.nr_id_transacao%type, nr_seq_filtro_p pls_pp_cta_filtro.nr_sequencia%type, ie_tipo_regra_p pls_pp_cta_combinada.ie_tipo_regra%type, ie_excecao_p pls_pp_cta_filtro.ie_excecao%type, tb_seq_conta_rec_p pls_util_cta_pck.t_number_table, tb_seq_resumo_rec_p pls_util_cta_pck.t_number_table, tb_seq_conta_proc_rec_p pls_util_cta_pck.t_number_table, tb_seq_conta_mat_rec_p pls_util_cta_pck.t_number_table, tb_seq_selecao_rec_p pls_util_cta_pck.t_number_table, ie_valido_p pls_pp_cta_rec_selecao.ie_valido%type) AS $body$
DECLARE

					
tb_atualizar_seqs_w	pls_util_cta_pck.t_number_table;
tb_gravar_conta_rec_w	pls_util_cta_pck.t_number_table;
tb_gravar_resumo_rec_w	pls_util_cta_pck.t_number_table;
tb_gravar_proc_rec_w	pls_util_cta_pck.t_number_table;
tb_gravar_mat_rec_w	pls_util_cta_pck.t_number_table;
nr_idx_atual_w		integer;
nr_idx_gravar_w		integer;

BEGIN

-- deve ter informacao da transacao para executar alguma alteracao.

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') and (tb_seq_conta_rec_p.count > 0) then

	-- quando nao for uma regra de excecao entao deve ser verificado os filtros para decidir se ira inserir ou atualizar.

	if (ie_excecao_p = 'N') then
	
		-- limpa as listas

		tb_atualizar_seqs_w.delete;
		tb_gravar_conta_rec_w.delete;
		tb_gravar_resumo_rec_w.delete;
		tb_gravar_proc_rec_w.delete;
		tb_gravar_mat_rec_w.delete;
		
		-- zerar os indices das listas de insercao e atualizacao.

		nr_idx_atual_w := 0;
		nr_idx_gravar_w := 0;
		
		-- verifica se tem registros no vetor de selecao

		if (tb_seq_selecao_rec_p.count > 0) then
		
			-- varrer os registros das listas passadas para verficar se ira inserir ou atualizar os registros.

			for i in tb_seq_selecao_rec_p.first .. tb_seq_selecao_rec_p.last loop
			
				-- se tiver informacao das sequencias do item ou dos filhos entao deve atualizar.

				if ((tb_seq_selecao_rec_p(i) IS NOT NULL AND (tb_seq_selecao_rec_p(i))::text <> '')) then
				
					-- joga para as variaveis que serao utilizadas para a atualizacao

					tb_atualizar_seqs_w(nr_idx_atual_w) := tb_seq_selecao_rec_p(i);
					
					-- incrementar o indice.

					nr_idx_atual_w := nr_idx_atual_w + 1;
					
				-- se nao e um novo registro e este deve ser inserido.

				else
					-- inserir um novo registro.

					tb_gravar_conta_rec_w(nr_idx_gravar_w) := tb_seq_conta_rec_p(i);
					tb_gravar_resumo_rec_w(nr_idx_gravar_w) := tb_seq_resumo_rec_p(i);
					tb_gravar_proc_rec_w(nr_idx_gravar_w) := tb_seq_conta_proc_rec_p(i);
					tb_gravar_mat_rec_w(nr_idx_gravar_w) := tb_seq_conta_mat_rec_p(i);
					
					-- incrementar o indice.

					nr_idx_gravar_w := nr_idx_gravar_w + 1;
				end if;
			end loop;
		end if;
		
		-- atualiza os registros passados por parametro no banco.

		CALL CALL CALL CALL CALL pls_filtro_regra_event_rgl_pck.atualizar_lista_filtro(tb_atualizar_seqs_w, ie_valido_p);
		
		-- se tiver algum registro para gravar entao manda ele pro banco.

		CALL CALL CALL CALL CALL pls_filtro_regra_event_rgl_pck.gravar_lista_filtro(	nr_id_transacao_p, nr_seq_filtro_p, ie_tipo_regra_p, tb_gravar_conta_rec_w,
					tb_gravar_resumo_rec_w, tb_gravar_proc_rec_w, tb_gravar_mat_rec_w, ie_valido_p);
					
	-- caso for uma regra de excecao entao atualiza o registro da selecao apenas, EXCECAO NAO INSERE NINGUEM.

	elsif (ie_excecao_p = 'S') then
		CALL CALL CALL CALL CALL pls_filtro_regra_event_rgl_pck.atualizar_lista_filtro(tb_seq_selecao_rec_p, ie_valido_p);
	end if;
end if;

END;

$body$
