CREATE OR REPLACE PROCEDURE pls_filtro_regra_event_rgl_pck.grava_regra_combinada_item ( nr_id_transacao_p pls_pp_cta_rec_selecao.nr_id_transacao%type, nr_seq_pp_evento_p pls_evento.nr_sequencia%type) AS $body$
DECLARE

					
tb_seq_resumo_rec_w		pls_util_cta_pck.t_number_table;

c01 CURSOR(nr_id_transacao_pc		pls_pp_cta_rec_selecao.nr_id_transacao%type) FOR
	SELECT	distinct a.nr_seq_resumo_rec
	from	pls_pp_cta_rec_selecao a
	where	a.nr_id_transacao = nr_id_transacao_pc
	and	a.ie_valido = 'S';
	

BEGIN

open c01(nr_id_transacao_p);
loop
	fetch c01 bulk collect into tb_seq_resumo_rec_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_seq_resumo_rec_w.count = 0;
	
	forall i in tb_seq_resumo_rec_w.first..tb_seq_resumo_rec_w.last
		update	pls_conta_rec_resumo_item a
		set	a.nr_seq_pp_evento = nr_seq_pp_evento_p
		where	a.nr_sequencia = tb_seq_resumo_rec_w(i)
		and	a.nr_seq_pp_evento = -1;
	commit;
end loop;
close c01;

END;

