SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_filtro_regra_event_rgl_pck.limpa_vinculo_regra_filtro ( nr_seq_analise_rec_p pls_analise_conta.nr_sequencia%type, nr_seq_prot_rec_p pls_rec_glosa_protocolo.nr_sequencia%type, nr_seq_cta_rec_p pls_rec_glosa_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, ie_tipo_limpeza_p text) AS $body$
DECLARE

					
tb_sequencia_w	pls_util_cta_pck.t_number_table;

C01 CURSOR(	nr_seq_analise_rec_pc	pls_analise_conta.nr_sequencia%type,
		nr_seq_prot_rec_pc	pls_rec_glosa_protocolo.nr_sequencia%type,
		nr_seq_cta_rec_pc	pls_rec_glosa_conta.nr_sequencia%type) FOR
	SELECT 	b.nr_sequencia
	from 	pls_rec_glosa_conta a,
		pls_conta_rec_resumo_item b
	where 	(nr_seq_analise_rec_pc IS NOT NULL AND nr_seq_analise_rec_pc::text <> '')
	and	a.nr_seq_analise = nr_seq_analise_rec_pc
	and 	b.nr_seq_conta_rec = a.nr_sequencia
	and 	b.ie_situacao = 'A'
	
union all

	SELECT 	b.nr_sequencia
	from 	pls_rec_glosa_conta a,
		pls_conta_rec_resumo_item b
	where 	(nr_seq_prot_rec_pc IS NOT NULL AND nr_seq_prot_rec_pc::text <> '')
	and	a.nr_seq_protocolo = nr_seq_prot_rec_pc
	and 	b.nr_seq_conta_rec = a.nr_sequencia
	and 	b.ie_situacao = 'A'
	
union all

	select	b.nr_sequencia
	from	pls_conta_rec_resumo_item b
	where 	(nr_seq_cta_rec_pc IS NOT NULL AND nr_seq_cta_rec_pc::text <> '')
	and	b.nr_seq_conta_rec = nr_seq_cta_rec_pc
	and 	b.ie_situacao = 'A';
	

BEGIN

begin
	open C01(nr_seq_analise_rec_p, nr_seq_prot_rec_p, nr_seq_cta_rec_p);
	loop
		fetch C01 bulk collect into tb_sequencia_w
		limit pls_util_pck.qt_registro_transacao_w;
		exit when tb_sequencia_w.count = 0;
		
		-- grava nas contas medicas resumo o valor '-1' no campo nr_seq_pp_evento para posteriormente fazer comparacoes

		-- utilizando o comando 'nr_seq_pp_evento = '-1' e nao 'nr_seq_pp_evento is null' nos registros que serao processados

		if (ie_tipo_limpeza_p = '-1') then
		
			forall	i in tb_sequencia_w.first..tb_sequencia_w.last
				update	pls_conta_rec_resumo_item
				set	nr_seq_pp_evento = -1
				where 	nr_sequencia = tb_sequencia_w(i);
				
		-- limpa o vinculo da regra que existe nos registros que foram processados, os registros que anteriormente 

		-- tiveram o nr_seq_pp_evento setado para '-1' e nao se encaixaram em nenhuma regra terao seu valor setado para null

		else	
			forall	i in tb_sequencia_w.first..tb_sequencia_w.last
				update	pls_conta_rec_resumo_item
				set	nr_seq_pp_evento  = NULL
				where 	nr_sequencia = tb_sequencia_w(i)
				and	nr_seq_pp_evento = -1;
		end if;
		commit;
	end loop;
	close C01;
exception
	when others then
	-- se deu algo e o cursor esta aberto fecha ele

	if (C01%isopen) then
		close C01;
	end if;
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
