CREATE OR REPLACE FUNCTION pls_filtro_regra_event_rgl_pck.obter_restricao_beneficiario ( ie_tipo_segurado_p pls_pp_cta_filtro_benef.ie_tipo_segurado%type, ds_campos_p out text, ds_tabela_p out text, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE


ds_restricao_benef_w	varchar(2500);
ds_alias_w		varchar(10);
ds_campos_w		varchar(500);
ds_tabela_w		varchar(200);
ds_alias_conta_orig_w	varchar(60);


BEGIN

ds_restricao_benef_w := null;
ds_campos_w := null;
ds_alias_w := 'seg';
ds_alias_conta_orig_w := pls_filtro_regra_event_rgl_pck.obter_alias_tabela('conta_original');

-- tipo beneficiario

if (ie_tipo_segurado_p IS NOT NULL AND ie_tipo_segurado_p::text <> '') then
	ds_restricao_benef_w := ds_restricao_benef_w || ' and nvl('|| ds_alias_conta_orig_w ||'.ie_tipo_segurado,'|| ds_alias_w ||'.ie_tipo_segurado)  = :ie_tipo_segurado_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(':ie_tipo_segurado_pc', ie_tipo_segurado_p, valor_bind_p);
end if;

if (ds_restricao_benef_w IS NOT NULL AND ds_restricao_benef_w::text <> '') then
	-- monta a ligacao

	ds_tabela_w := ds_tabela_w || ', ' || pls_util_pck.enter_w ||
			'	pls_segurado ' || ds_alias_w;
		
	ds_restricao_benef_w := ' and ' || ds_alias_w || '.nr_sequencia = ' || ds_alias_conta_orig_w || '.nr_seq_segurado ' ||
				pls_util_pck.enter_w || ds_restricao_benef_w;


	ds_tabela_p := ds_tabela_w;
	ds_campos_p := ds_campos_w;
end if;

return;

END;

