CREATE OR REPLACE PROCEDURE pls_filtro_regra_preco_cta_pck.atualiza_sel_excecao_end ( nr_id_transacao_p pls_cp_cta_selecao.nr_id_transacao%type, ie_excecao_p pls_cp_cta_filtro.ie_excecao%type) AS $body$
BEGIN
-- s_ atualiza se for exce__o

if (ie_excecao_p = 'S') then
	-- S_ atualiza se valido.

	if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then
		
		-- Descarta que _ excecao.

		update	pls_cp_cta_selecao
		set	ie_valido = 'N',
			ie_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao = 'S';
		
		commit;
	end if;
end if;

END;

