CREATE OR REPLACE PROCEDURE pls_filtro_regra_preco_cta_pck.gerencia_selecao_filtro ( nr_id_transacao_p pls_cp_cta_selecao.nr_id_transacao%type, nr_seq_filtro_p pls_cp_cta_filtro.nr_sequencia%type, ie_tipo_regra_p pls_cp_cta_combinada.ie_tipo_regra%type, ie_excecao_p pls_cp_cta_filtro.ie_excecao%type, tb_seq_conta_p pls_util_cta_pck.t_number_table, tb_seq_conta_proc_p pls_util_cta_pck.t_number_table, tb_seq_conta_mat_p pls_util_cta_pck.t_number_table, tb_nr_seq_partic_p pls_util_cta_pck.t_number_table, tb_seq_selecao_p pls_util_cta_pck.t_number_table, ie_valido_p pls_cp_cta_selecao.ie_valido%type) AS $body$
DECLARE


tb_atualizar_seqs_w		pls_util_cta_pck.t_number_table;
tb_gravar_conta_w		pls_util_cta_pck.t_number_table;
tb_gravar_proc_w		pls_util_cta_pck.t_number_table;
tb_gravar_mat_w			pls_util_cta_pck.t_number_table;
tb_gravar_seq_partic_w		pls_util_cta_pck.t_number_table;

nr_idx_atual_w		integer;
nr_idx_gravar_w		integer;

BEGIN
-- Deve ter informa__o da transa__o para executar alguma altera__o.

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') and (tb_seq_conta_p.count > 0) then

	-- Quando n_o for uma regra de exce__o ent_o deve ser verificado os filtros para decidir se ir_ inserir ou atualizar.

	if (ie_excecao_p = 'N') then
	
		-- limpa as listas

		tb_atualizar_seqs_w.delete;
		tb_gravar_conta_w.delete;
		tb_gravar_proc_w.delete;
		tb_gravar_mat_w.delete;
		tb_gravar_seq_partic_w.delete;
		
		-- Zerar os indices das listas de inser__o e atualiza__o.

		nr_idx_atual_w := 0;
		nr_idx_gravar_w := 0;

		-- verifica se tem registros no vetor de sele__o

		if (tb_seq_selecao_p.count > 0) then

			-- Varrer os registros das listas passadas para verficar se ir_ inserir ou atualizar os registros.

			for i in tb_seq_selecao_p.first .. tb_seq_selecao_p.last loop

				-- Se tiver informa__o das sequencias do item ou dos filhos ent_o deve atualizar.

				if ((tb_seq_selecao_p(i) IS NOT NULL AND (tb_seq_selecao_p(i))::text <> '')) then

					-- joga para as vari_veis que ser_o utilizadas para a atualizacao

					tb_atualizar_seqs_w(nr_idx_atual_w) := tb_seq_selecao_p(i);
					
					-- Incrementar o indice.

					nr_idx_atual_w := nr_idx_atual_w + 1;

				-- Se n_o _ um novo registro e este deve ser inserido.

				else
					-- Inserir um novo registro.

					tb_gravar_conta_w(nr_idx_gravar_w) := tb_seq_conta_p(i);
					tb_gravar_proc_w(nr_idx_gravar_w) := tb_seq_conta_proc_p(i);
					tb_gravar_mat_w(nr_idx_gravar_w) := tb_seq_conta_mat_p(i);
					tb_gravar_seq_partic_w(nr_idx_gravar_w) := tb_nr_seq_partic_p(i);
					
					-- Incrementar o indice.

					nr_idx_gravar_w := nr_idx_gravar_w + 1;
				end if;
			end loop;
		end if;

		-- Atualiza os registros passados por par_metro no banco.

		CALL CALL CALL CALL CALL pls_filtro_regra_preco_cta_pck.atualizar_lista_filtro(	tb_atualizar_seqs_w, ie_valido_p);

		-- Se tiver algum registro para gravar ent_o manda ele pro banco.

		CALL CALL CALL CALL CALL pls_filtro_regra_preco_cta_pck.gravar_lista_filtro(	nr_id_transacao_p, nr_seq_filtro_p,
					ie_tipo_regra_p, tb_gravar_conta_w,
					tb_gravar_proc_w, tb_gravar_mat_w,
					tb_gravar_seq_partic_w,
					ie_valido_p);

	-- Caso for uma regra de exce__o entao atualiza o registro  da selecao apenas, EXECAO NAO INSERE NINGUEM.

	elsif (ie_excecao_p = 'S') then
		CALL CALL CALL CALL CALL pls_filtro_regra_preco_cta_pck.atualizar_lista_filtro(	tb_seq_selecao_p, ie_valido_p);
	end if;
end if;

END;

$body$
