CREATE OR REPLACE FUNCTION pls_filtro_regra_preco_cta_pck.obter_alias_tabela ( ds_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_alias_w	varchar(10);


BEGIN
-- retorna o alias que ser_ utilizado no select de acordo com a tabela

case(ds_tabela_p)

	when 'protocolo' then
		ds_alias_w := 'prot';

	when 'conta' then
		ds_alias_w := 'conta';

	when 'procedimento' then
		ds_alias_w := 'proc';

	when 'participante' then
		ds_alias_w := 'partic';
	
	when 'material' then
		ds_alias_w := 'mat';
		
	when 'selecao' then
		ds_alias_w := 'sele';
	
	when 'proc_regra' then
		ds_alias_w := 'proc_regra';
		
	when 'mat_regra' then
		ds_alias_w := 'mat_regra';
	
	else
		ds_alias_w := null;
end case;

return ds_alias_w;

END;

