CREATE OR REPLACE FUNCTION pls_filtro_regra_preco_cta_pck.obter_restricao_contrato ( nr_seq_grupo_contrato_p pls_cp_cta_filtro_cont.nr_seq_grupo_contrato%type, nr_seq_contrato_p pls_cp_cta_filtro_cont.nr_seq_contrato%type, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

					
dados_restricao_w	varchar(500);
ds_restricao_seg_w	varchar(2500);
ds_alias_w		varchar(10);


BEGIN

ds_alias_w := 'seg';

-- Inicializar o retorno

dados_restricao_w := 	'	and	exists(select	1 ' || pls_util_pck.enter_w||
			'			from	pls_segurado ' || ds_alias_w || pls_util_pck.enter_w||
			'			where	' || ds_alias_w || '.nr_sequencia = ' || pls_filtro_regra_preco_cta_pck.obter_alias_tabela('conta') || '.nr_seq_segurado ' || pls_util_pck.enter_w;

-- Grupo contrato

if (nr_seq_grupo_contrato_p IS NOT NULL AND nr_seq_grupo_contrato_p::text <> '') then
	
	ds_restricao_seg_w := ds_restricao_seg_w || '	and exists(select 1 ' || pls_util_pck.enter_w ||
						'		from 	pls_preco_contrato x ' || pls_util_pck.enter_w ||
						'		where	x.nr_seq_grupo = :nr_seq_grupo_contrato_pc ' || pls_util_pck.enter_w ||
						'		and	x.nr_seq_contrato = ' || ds_alias_w || '.nr_seq_contrato) ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_grupo_contrato_pc', nr_seq_grupo_contrato_p, valor_bind_p);
end if;

-- Sequencia do contrato

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then

	ds_restricao_seg_w := ds_restricao_seg_w || '	and	' || ds_alias_w || '.nr_seq_contrato = :nr_seq_contrato_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_contrato_pc', nr_seq_contrato_p, valor_bind_p);
end if;

-- somente processa o filtro caso tenha algum campo informado, se for regra em branco NAO precisa do filtro

if (ds_restricao_seg_w IS NOT NULL AND ds_restricao_seg_w::text <> '') then

	ds_restricao_seg_w := dados_restricao_w || ds_restricao_seg_w || ' ) ';
end if;

return;

END;

$body$
