CREATE OR REPLACE FUNCTION pls_filtro_regra_preco_cta_pck.obter_restricao_produto ( nr_seq_tipo_acomod_p pls_cp_cta_filtro_produto.nr_seq_tipo_acomodacao%type, nr_seq_categ_plano_p pls_cp_cta_filtro_produto.nr_seq_categoria_plano%type, ie_tipo_contratacao_p pls_cp_cta_filtro_produto.ie_tipo_contratacao%type, ie_preco_p pls_cp_cta_filtro_produto.ie_preco%type, nr_seq_grupo_produto_p pls_cp_cta_filtro_produto.nr_seq_grupo_produto%type, nr_seq_plano_p pls_cp_cta_filtro_produto.nr_seq_plano%type, ie_acomodacao_p pls_cp_cta_filtro_produto.ie_acomodacao%type, ie_franquia_p pls_cp_cta_filtro_produto.ie_franquia%type, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE


dados_restricao_w	varchar(500);
ds_alias_w		varchar(10);


BEGIN

ds_alias_w := 'prod';

-- Inicializar o retorno

dados_restricao_w := 	'	and	exists(select	1 ' || pls_util_pck.enter_w||
			'			from	pls_cp_cta_segurado_plano_v ' || ds_alias_w || pls_util_pck.enter_w||
			'			where	' || ds_alias_w || '.nr_sequencia = ' || pls_filtro_regra_preco_cta_pck.obter_alias_tabela('conta') || '.nr_seq_segurado ' || pls_util_pck.enter_w;

-- Produto

if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
	
	dados_restricao_w := dados_restricao_w || '	and 	' || ds_alias_w || '.nr_seq_plano = :nr_seq_plano_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_plano_pc', nr_seq_plano_p, valor_bind_p);
end if;

-- Grupo de produto

if (nr_seq_grupo_produto_p IS NOT NULL AND nr_seq_grupo_produto_p::text <> '') then

	dados_restricao_w := dados_restricao_w || 	' 	and exists (	select	1 ' || pls_util_pck.enter_w ||
							'			from	pls_preco_produto x ' || pls_util_pck.enter_w ||
							'			where	x.nr_seq_plano = ' || ds_alias_w || '.nr_seq_plano ' || pls_util_pck.enter_w ||
							'			and	x.nr_seq_grupo = :nr_seq_grupo_produto_pc) ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_grupo_produto_pc', nr_seq_grupo_produto_p, valor_bind_p);
end if;

-- Preco

if (ie_preco_p IS NOT NULL AND ie_preco_p::text <> '') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.ie_preco = :ie_preco_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':ie_preco_pc', ie_preco_p, valor_bind_p);
end if;

-- Tipo de acomodacao

if (nr_seq_tipo_acomod_p IS NOT NULL AND nr_seq_tipo_acomod_p::text <> '') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.nr_seq_tipo_acomodacao = :nr_seq_tipo_acomod_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_tipo_acomod_pc', nr_seq_tipo_acomod_p, valor_bind_p);
end if;

-- Categoria do plano

if (nr_seq_categ_plano_p IS NOT NULL AND nr_seq_categ_plano_p::text <> '') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.nr_seq_categoria = :nr_seq_categ_plano_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_categ_plano_pc', nr_seq_categ_plano_p, valor_bind_p);
end if;

-- Tipo contratacao

if (ie_tipo_contratacao_p IS NOT NULL AND ie_tipo_contratacao_p::text <> '') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.ie_tipo_contratacao = :ie_tipo_contratacao_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':ie_tipo_contratacao_pc', ie_tipo_contratacao_p, valor_bind_p);
end if;

-- Padrao de acomodacao

if (ie_acomodacao_p IS NOT NULL AND ie_acomodacao_p::text <> '') and (ie_acomodacao_p <> 'N') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.ie_acomodacao = :ie_acomodacao_pc ' || pls_util_pck.enter_w;
	valor_bind_p := sql_pck.bind_variable(	':ie_acomodacao_pc', ie_acomodacao_p, valor_bind_p);
end if;

-- Fator moderador - Franquia, so e verificado caso o checkbox esteja selecionado

if (ie_franquia_p = 'S') then

	dados_restricao_w := dados_restricao_w || '	and	' || ds_alias_w || '.ie_franquia = ''S'' ' || pls_util_pck.enter_w;
end if;

dados_restricao_w := dados_restricao_w || ' ) ';

return;

END;

$body$
