CREATE OR REPLACE FUNCTION pls_filtro_regra_preco_cta_pck.usar_nova_regra_crit_preco ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_regra_preco_nova_w	varchar(1);


BEGIN

-- se a data da _ltima solicita__o n_o estiver registrada ou 

-- se j_ se passou 67 segundos deste a _ltima solicita__o ou

-- se o estabelecimento anterior for diferente do _ltimo solicitado

if	((current_setting('pls_filtro_regra_preco_cta_pck.dt_ultima_solicitacao_w')::coalesce(timestamp::text, '') = '') or
	(current_setting('pls_filtro_regra_preco_cta_pck.dt_ultima_solicitacao_w')::timestamp <= (clock_timestamp() - interval '67 seconds')) or (coalesce(current_setting('pls_filtro_regra_preco_cta_pck.cd_estabelecimento_ultimo_w')::estabelecimento.cd_estabelecimento%type, -1) != coalesce(cd_estabelecimento_p, -2))) then

	-- se tiver estabelecimento filtra por estabelecimento

	if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	
		select	max(ie_regra_preco_nova)
		into STRICT	ie_regra_preco_nova_w
		from	pls_visible_false
		where	cd_estabelecimento = cd_estabelecimento_p;
	end if;
	
	-- se n_o encontrou nada acima busca sem considerar estabelecimento

	if (coalesce(ie_regra_preco_nova_w::text, '') = '') then
	
		select	max(ie_regra_preco_nova)
		into STRICT	ie_regra_preco_nova_w
		from	pls_visible_false;
	end if;

	-- se estiver nulo ent_o n_o _ a nova

	if (coalesce(ie_regra_preco_nova_w::text, '') = '') then
	
		ie_regra_preco_nova_w := 'N';
	end if;

	PERFORM set_config('pls_filtro_regra_preco_cta_pck.cd_estabelecimento_ultimo_w', cd_estabelecimento_p, false);
	PERFORM set_config('pls_filtro_regra_preco_cta_pck.dt_ultima_solicitacao_w', clock_timestamp(), false);
	PERFORM set_config('pls_filtro_regra_preco_cta_pck.ie_ultimo_retorno_regra_w', ie_regra_preco_nova_w, false);
else
	-- se n_o fizer a consulta retorna o que retornou por _ltimo

	ie_regra_preco_nova_w := current_setting('pls_filtro_regra_preco_cta_pck.ie_ultimo_retorno_regra_w')::varchar(1);
end if;

return ie_regra_preco_nova_w;

END;

$body$
