CREATE OR REPLACE FUNCTION pls_fiscal_dados_dmed_pck.obter_valor_desconto_tit_pag ( vl_desconto_p bigint) RETURNS bigint AS $body$
DECLARE

	
vl_desconto_baixa_tit_pag_w	double precision;


BEGIN

vl_desconto_baixa_tit_pag_w	:= 0;

if (vl_desconto_p <> 0) then
	vl_desconto_baixa_tit_pag_w	:= (current_setting('pls_fiscal_dados_dmed_pck.pr_item_w')::double/100) * vl_desconto_p;
	
	if (current_setting('pls_fiscal_dados_dmed_pck.qt_item_w')::bigint = current_setting('pls_fiscal_dados_dmed_pck.qt_total_itens_w')::bigint) then
		if	((current_setting('pls_fiscal_dados_dmed_pck.vl_total_desconto_w')::titulo_receber_liq.vl_descontos%type + vl_desconto_baixa_tit_pag_w) <> vl_desconto_p) then
			vl_desconto_baixa_tit_pag_w	:= vl_desconto_baixa_tit_pag_w + (vl_desconto_p - (current_setting('pls_fiscal_dados_dmed_pck.vl_total_desconto_w')::titulo_receber_liq.vl_descontos%type + vl_desconto_baixa_tit_pag_w));
		end if;
	end if;
end if;

PERFORM set_config('pls_fiscal_dados_dmed_pck.vl_total_desconto_w', current_setting('pls_fiscal_dados_dmed_pck.vl_total_desconto_w')::titulo_receber_liq.vl_descontos%type + vl_desconto_baixa_tit_pag_w, false);

return vl_desconto_baixa_tit_pag_w;

END;

