				vl_juros_multa_w	:= vl_juros_multa_w + (vl_juros_multa_p - (vl_juros_multa_w + current_setting('pls_fiscal_dados_dmed_pck.vl_total_juros_w')::double));
			end if;
		elsif (ie_juros_multa_p = 'M') then
			if	((vl_juros_multa_w + current_setting('pls_fiscal_dados_dmed_pck.vl_total_multa_w')::double) <> vl_juros_multa_p) then
				vl_juros_multa_w	:= vl_juros_multa_w + (vl_juros_multa_p - (vl_juros_multa_w + current_setting('pls_fiscal_dados_dmed_pck.vl_total_multa_w')::double));
			end if;
		end if;
	end if;
