if (ie_juros_multa_p = 'J') then
	PERFORM set_config('pls_fiscal_dados_dmed_pck.vl_total_juros_w', current_setting('pls_fiscal_dados_dmed_pck.vl_total_juros_w')::double + vl_juros_multa_w, false);
