CREATE OR REPLACE FUNCTION pls_fiscal_dados_dmed_pck.obter_valor_rec_maior ( vl_rec_maior_p titulo_receber_liq.vl_rec_maior%type) RETURNS bigint AS $body$
DECLARE


vl_rec_maior_w		double precision;


BEGIN

vl_rec_maior_w	:= 0;

if (vl_rec_maior_p <> 0) then
	vl_rec_maior_w	:= (current_setting('pls_fiscal_dados_dmed_pck.pr_item_w')::double/100) * vl_rec_maior_p;
	
	if (current_setting('pls_fiscal_dados_dmed_pck.qt_item_w')::bigint = current_setting('pls_fiscal_dados_dmed_pck.qt_total_itens_w')::bigint) then
		if	((current_setting('pls_fiscal_dados_dmed_pck.vl_total_rec_maior_w')::double + vl_rec_maior_w) <> vl_rec_maior_p) then
			vl_rec_maior_w := vl_rec_maior_w + (vl_rec_maior_p - (current_setting('pls_fiscal_dados_dmed_pck.vl_total_rec_maior_w')::double + vl_rec_maior_w));
		end if;
	end if;
end if;

PERFORM set_config('pls_fiscal_dados_dmed_pck.vl_total_rec_maior_w', current_setting('pls_fiscal_dados_dmed_pck.vl_total_rec_maior_w')::double + vl_rec_maior_w, false);

return vl_rec_maior_w;

END;

