--obter_regra_autorizacao
CREATE OR REPLACE FUNCTION pls_ger_envio_anexos_ans_pck.obter_regra_autorizacao ( nr_seq_guia_plano_p pls_guia_plano.nr_sequencia%type, cd_tipo_documento_p pls_tiss_tipo_documento.cd_tipo_documento%type) RETURNS PLS_TISS_TIPO_DOCUMENTO.NR_SEQUENCIA%TYPE AS $body$
DECLARE

									
									
nr_seq_regra_w	pls_tiss_tipo_documento.nr_sequencia%type;									


BEGIN

	select max(a.nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_tiss_tipo_documento a,
			pls_guia_plano b
	where 	b.nr_sequencia = nr_seq_guia_plano_p	
	and ( coalesce(a.cd_tipo_documento::text, '') = '' or (a.cd_tipo_documento = cd_tipo_documento_p))
	and (coalesce(a.ie_tipo_guia_aut::text, '') = '' or (a.ie_tipo_guia_aut = b.ie_tipo_guia))
	and 	( (coalesce(a.ie_status_aut::text, '') = '') or (a.ie_status_aut = b.ie_status))
	and 	a.ie_situacao = 'A'
	and 	a.ie_tipo_regra = 'A';
									
return nr_seq_regra_w;									

END;

$body$
