CREATE OR REPLACE FUNCTION pls_ger_envio_anexos_ans_pck.obter_regra_cta_medica ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, cd_tipo_documento_p pls_tiss_tipo_documento.cd_tipo_documento%type) RETURNS PLS_TISS_TIPO_DOCUMENTO.NR_SEQUENCIA%TYPE AS $body$
DECLARE

									
									
nr_seq_regra_w	pls_tiss_tipo_documento.nr_sequencia%type;									
ie_tipo_guia_w	pls_conta.ie_tipo_guia%type;
ie_status_w		pls_conta.ie_status%type;
									

BEGIN

	if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	
		select  max(b.ie_tipo_guia),
				max(b.ie_status)
		into STRICT	ie_tipo_guia_w,
				ie_status_w
		from 	pls_conta b
		where 	b.nr_sequencia = nr_seq_conta_p;
	
	
		select max(a.nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_tiss_tipo_documento a		
		where ( coalesce(a.cd_tipo_documento::text, '') = '' or (a.cd_tipo_documento = cd_tipo_documento_p))
		and (coalesce(a.ie_tipo_guia::text, '') = '' or (a.ie_tipo_guia = ie_tipo_guia_w))
		and 	( (coalesce(a.ie_status::text, '') = '') or (a.ie_status = ie_status_w))
		and		a.ie_situacao = 'A'
		and 	a.ie_tipo_regra = 'C';
	else
	
		select max(a.nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_tiss_tipo_documento a,
				pls_protocolo_conta b
		where 	b.nr_sequencia = nr_seq_protocolo_p
		and (coalesce(a.cd_tipo_documento::text, '') = '' or (a.cd_tipo_documento = cd_tipo_documento_p))
		and (coalesce(a.ie_tipo_guia::text, '') = '' or (b.ie_tipo_guia = a.ie_tipo_guia))
		and		a.ie_situacao = 'A'
		and 	a.ie_tipo_regra = 'C';

	end if;
									
return nr_seq_regra_w;									

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
