CREATE OR REPLACE FUNCTION pls_ger_envio_anexos_ans_pck.obter_regra_recurso ( nr_seq_prot_rec_p pls_rec_glosa_protocolo.nr_sequencia%type, cd_tipo_documento_p pls_tiss_tipo_documento.cd_tipo_documento%type) RETURNS PLS_TISS_TIPO_DOCUMENTO.NR_SEQUENCIA%TYPE AS $body$
DECLARE

								
nr_seq_regra_w	pls_tiss_tipo_documento.nr_sequencia%type;									
								

BEGIN

	select max(a.nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_tiss_tipo_documento a,
			pls_rec_glosa_protocolo  b
	where 	b.nr_sequencia = nr_seq_prot_rec_p
	and ( coalesce(a.cd_tipo_documento::text, '') = '' or (a.cd_tipo_documento = cd_tipo_documento_p))
	and 	( (coalesce(a.ie_status_recurso::text, '') = '') or (a.ie_status_recurso = b.ie_status))
	and 	a.ie_situacao = 'A'
	and 	a.ie_tipo_regra = 'R';
	
	return nr_seq_regra_w;
				

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
