CREATE OR REPLACE PROCEDURE pls_ger_envio_anexos_ans_pck.validar_anexo_rec_glosa ( nr_seq_prot_rec_p pls_rec_glosa_protocolo.nr_sequencia%type, nr_seq_prestador_p pls_anexo_imp.nr_seq_prestador%type, cd_tipo_documento_p pls_anexo_imp.cd_tipo_documento%type, nr_seq_prot_rec_retorno_p INOUT pls_rec_glosa_protocolo.nr_sequencia%type, cd_motivo_glosa_p INOUT pls_anexo_imp.cd_motivo_glosa%type, ds_motivo_glosa_p INOUT pls_anexo_imp.ds_motivo_glosa%type, ie_status_p INOUT pls_anexo_imp.ie_status%type) AS $body$
DECLARE


nr_seq_rec_w	pls_rec_glosa_protocolo.nr_sequencia%type;									
									

BEGIN

	if ( coalesce(nr_seq_prot_rec_p::text, '') = '') then
		cd_motivo_glosa_p := '3162';
		ds_motivo_glosa_p := obter_texto_dic_objeto(1211643, wheb_usuario_pck.get_nr_seq_idioma, null);
		ie_status_p := 'G';		
	else
	
		select 	max(a.nr_sequencia)
		into STRICT	nr_seq_rec_w
		from	pls_rec_glosa_protocolo a
		where 	nr_sequencia = nr_seq_prot_rec_p
		and (coalesce(nr_seq_prestador_p::text, '') = '' or a.nr_seq_prestador = nr_seq_prestador_p);
		
		if ( coalesce(nr_seq_rec_w::text, '') = '') then
			cd_motivo_glosa_p := '3162';
			ds_motivo_glosa_p := obter_texto_dic_objeto(1211643, wheb_usuario_pck.get_nr_seq_idioma, null);
			ie_status_p := 'G';	
		else
			--se encontrou prot de recurso a partir das informacoes passadas na entrada, entao faz a leitura das regras para o recurso
			if ( coalesce(pls_ger_envio_anexos_ans_pck.obter_regra_recurso( nr_seq_prot_rec_p, cd_tipo_documento_p)::text, '') = '')then		
				cd_motivo_glosa_p := '3163';	
				ds_motivo_glosa_p := obter_texto_dic_objeto( 1211644, wheb_usuario_pck.get_nr_seq_idioma, null);				
				ie_status_p := 'G';	
			else
				--no caso do recurso, retorna o proprio protocolo de entrada, caso o mesmo seja localizado na base
				nr_seq_prot_rec_retorno_p := nr_seq_prot_rec_p;
			end if;
		end if;
	end if;
	
END;									

$body$
