CREATE OR REPLACE PROCEDURE pls_gerar_arquivo_ame_pck.gerar_arquivo (nr_seq_lote_p pls_ame_lote_remessa.nr_sequencia%type, ie_origem_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ds_local_w		evento_tasy_utl_file.ds_local%type;
dt_ref_mensalidade_w	pls_ame_lote_remessa.dt_ref_mensalidade%type;
dt_lote_w		pls_ame_lote_remessa.dt_lote%type;

C01 CURSOR(	nr_seq_lote_pc		pls_ame_lote_remessa.nr_sequencia%type,
		dt_ref_mensalidade_pc	pls_ame_lote_remessa.dt_ref_mensalidade%type,
		dt_lote_pc		pls_ame_lote_remessa.dt_lote%type) FOR
	SELECT	b.nr_sequencia nr_seq_rem_arq,
		c.nr_seq_regra_layout,
		a.nr_sequencia,
		c.nr_sequencia nr_seq_regra_ame_dest,
		pls_ame_pck.substituir_macro_nm_arquivo(c.nm_arquivo, dt_ref_mensalidade_pc, pls_ame_pck.obter_dt_competencia_copartic(a.nr_sequencia), nr_seq_lote_pc, dt_lote_pc) nm_arquivo
	from	pls_ame_lote_rem_destino a,
		pls_ame_lote_rem_arquivo b,
		pls_ame_regra_ger_arq c
	where	a.nr_sequencia = b.nr_seq_lote_rem_dest
	and	c.nr_sequencia = b.nr_seq_regra_ger_arquivo
	and	(c.nr_seq_regra_layout IS NOT NULL AND c.nr_seq_regra_layout::text <> '')
	and	a.nr_seq_lote_rem = nr_seq_lote_p;

BEGIN

PERFORM set_config('pls_gerar_arquivo_ame_pck.nr_seq_lote_w', nr_seq_lote_p, false);
PERFORM set_config('pls_gerar_arquivo_ame_pck.nm_usuario_w', nm_usuario_p, false);
PERFORM set_config('pls_gerar_arquivo_ame_pck.ie_origem_w', coalesce(ie_origem_p, 'H'), false);

select	dt_ref_mensalidade,
	dt_lote
into STRICT	dt_ref_mensalidade_w,
	dt_lote_w
from	pls_ame_lote_remessa
where	nr_sequencia = current_setting('pls_gerar_arquivo_ame_pck.nr_seq_lote_w')::pls_ame_lote_remessa.nr_sequencia%type;

if (current_setting('pls_gerar_arquivo_ame_pck.ie_origem_w')::varchar(1) = 'H') then
	delete from pls_ame_export_arq where TRUNC(dt_atualizacao, 'dd') < TRUNC(clock_timestamp() - interval '2 days', 'dd');
elsif (current_setting('pls_gerar_arquivo_ame_pck.ie_origem_w')::varchar(1) = 'D') then
	select	max(ds_local)
	into STRICT	ds_local_w
	from	evento_tasy_utl_file
	where	cd_evento	= 5;

	if (coalesce(ds_local_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(285471);
	end if;
end if;
	
for r_c01_w in c01(	current_setting('pls_gerar_arquivo_ame_pck.nr_seq_lote_w')::pls_ame_lote_remessa.nr_sequencia%type,
			dt_ref_mensalidade_w,
			dt_lote_w) loop
	begin
	if (current_setting('pls_gerar_arquivo_ame_pck.ie_origem_w')::varchar(1) = 'D') then
		begin
		PERFORM set_config('pls_gerar_arquivo_ame_pck.arq_texto_w', utl_file.fopen(ds_local_w,r_c01_w.nm_arquivo,'W'), false);
		exception
		when others then
			if (SQLSTATE = -29289) then
				--O acesso ao arquivo foi negado pelo sistema operacional (access_denied).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302935);
			elsif (SQLSTATE = -29298) then
				--O arquivo foi aberto usando FOPEN_NCHAR,  mas efetuaram-se operacoes de I/O usando funcoes nonchar comos PUTF ou GET_LINE (charsetmismatch).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302936);
			elsif (SQLSTATE = -29291) then
				--Nao foi possivel apagar o arquivo (delete_failed).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302937);
			elsif (SQLSTATE = -29286) then
				--Erro interno desconhecido no package UTL_FILE (internal_error).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302938);
			elsif (SQLSTATE = -29282) then
				--O handle do arquivo nao existe (invalid_filehandle).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302939);
			elsif (SQLSTATE = -29288) then
				--O arquivo com o nome especificado nao foi encontrado neste local (invalid_filename).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302940);
			elsif (SQLSTATE = -29287) then
				--O valor de MAX_LINESIZE para FOPEN() e invalido; deveria estar na faixa de 1 a 32767 (invalid_maxlinesize).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302942);
			elsif (SQLSTATE = -29281) then
				--O parametro open_mode na chamda FOPEN e invalido (invalid_mode).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302943);
			elsif (SQLSTATE = -29290) then
				--O parametro ABSOLUTE_OFFSET para a chamada FSEEK() e invalido; deveria ser maior do que 0 e menor do que o numero total de bytes do arquivo (invalid_offset).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302944);
			elsif (SQLSTATE = -29283) then
				--O arquivo nao pode ser aberto ou operado da forma desejada - ou o caminho nao foi encontrado (invalid_operation).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302945);
			elsif (SQLSTATE = -29280) then
				--O caminho especificado nao existe ou nao esta visivel ao Oracle (invalid_path).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302946);
			elsif (SQLSTATE = -29284) then
				--Nao e possivel efetuar a leitura do arquivo (read_error).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302947);
			elsif (SQLSTATE = -29292) then
				--Nao e possivel renomear o arquivo.

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302948);
			elsif (SQLSTATE = -29285) then
				--Nao foi possivel gravar no arquivo (write_error).

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302950);
			else
				--Erro desconhecido no package UTL_FILE.

				CALL wheb_mensagem_pck.exibir_mensagem_abort(302951);
			end if;
		end;
	end if;
	
	-- Carrega as informacoes da regra

	CALL pls_gerar_arquivo_ame_pck.carregar_regra(r_c01_w.nr_seq_regra_layout);

	if (current_setting('pls_gerar_arquivo_ame_pck.ie_origem_w')::varchar(1) = 'H') then
		delete from pls_ame_export_arq where nr_seq_lote_rem_arquivo = r_c01_w.nr_seq_rem_arq and nm_usuario = current_setting('pls_gerar_arquivo_ame_pck.nm_usuario_w')::usuario.nm_usuario%type;
	end if;
	
	--Inserir

	CALL pls_gerar_arquivo_ame_pck.criar_registros(r_c01_w.nr_seq_rem_arq);

	if (current_setting('pls_gerar_arquivo_ame_pck.ie_origem_w')::varchar(1) = 'D') then
		utl_file.fclose(current_setting('pls_gerar_arquivo_ame_pck.arq_texto_w')::utl_file.file_type);
	end if;
	end;
end loop;

update	pls_ame_lote_remessa
set	dt_geracao_arquivos	= clock_timestamp()
where	nr_sequencia		= nr_seq_lote_p;

-- Salva a transacao,

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
