		nr_vetor_w	:= current_setting('pls_gerar_boletos_mens_fat_pck.reg_faixas_etarias_w')::reg_faixas_etarias_v.count+1;
		current_setting('pls_gerar_boletos_mens_fat_pck.reg_faixas_etarias_w')::reg_faixas_etarias_v[nr_vetor_w].qt_idade_inicial	:= qt_idade_inicial_w;
		current_setting('pls_gerar_boletos_mens_fat_pck.reg_faixas_etarias_w')::reg_faixas_etarias_v[nr_vetor_w].qt_idade_final		:= qt_idade_final_w;
		end;
	end loop;
	close C00;

	end;


