SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerar_boletos_mens_fat_pck.armezenar_dados_info_boleto () AS $body$
DECLARE


	nr_seq_tipo_info_boleto_w		pls_tipo_informacao_boleto.nr_sequencia%type;
	nm_relatorio_w				pls_tipo_informacao_boleto.nm_relatorio%type;
	ds_sql_select_w				varchar(255);
	index_w					bigint := 0;

	C01 CURSOR FOR
		SELECT	nr_sequencia,
			nm_relatorio
		from	pls_tipo_informacao_boleto
		where	ie_situacao	= 'A';

	
BEGIN

	open C01;
	loop
	fetch C01 into
		nr_seq_tipo_info_boleto_w,
		nm_relatorio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		index_w			:= index_w + 1;
		ds_sql_select_w	:= '';

		current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto	:= nr_seq_tipo_info_boleto_w;
		current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nm_relatorio		:= nm_relatorio_w;

		/*TITULARIDADE*/

		if (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 7) then
			ds_sql_select_w	:= ' decode(c.nr_seq_titular,null,''TITULAR'',''DEPENDENTE'') ';
		/*TIPO PARENTESCO*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 9) then
			ds_sql_select_w	:= ' decode(c.nr_seq_titular,null,''TITULAR'',decode(pls_obter_garu_dependencia_seg(c.nr_sequencia,''C''),''DL'',''DEPENDENTE'',''AGREGADO'')) ';
		/*PRODUTO*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 11) then
			ds_sql_select_w	:= ' d.ds_plano ';
		/*FAIXA ETÁRIA INICIAL*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 12) then
			ds_sql_select_w	:= ' pls_gerar_boletos_mens_fat_pck.obter_faixa_etaria_benef(b.qt_idade,''I'') ';
		/*FAIXA ETÁRIA FINAL*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 18) then
			ds_sql_select_w	:= ' pls_gerar_boletos_mens_fat_pck.obter_faixa_etaria_benef(b.qt_idade,''F'') ';
		/*SCA*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 13) then
			ds_sql_select_w	:= ' substr(pls_obter_dados_vinc_sca(a.nr_seq_vinculo_sca,''N''),1,255) ';
		/*VALOR ITEM UNITARIO*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 14) then
			ds_sql_select_w	:= ' a.vl_item ';
		/*VALOR TOTAL ITEM*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 16) then
			ds_sql_select_w	:= ' sum(a.vl_item) ';
		/*QUANTIDADE REGISTROS ITEM*/

		elsif (current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].nr_seq_tipo_info_boleto = 17) then
			ds_sql_select_w	:= ' count(1) ';
		end if;

		current_setting('pls_gerar_boletos_mens_fat_pck.tp_info_boleto_vetor_w')::tp_info_boleto_v[index_w].ds_sql_select		:= ds_sql_select_w;

		end;
	end loop;
	close C01;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_boletos_mens_fat_pck.armezenar_dados_info_boleto () FROM PUBLIC;
