CREATE OR REPLACE PROCEDURE pls_gerar_boletos_mens_fat_pck.criar_boletos_bandas ( nr_seq_boleto_p pls_boleto.nr_sequencia%type, item_mensalidade_fatumento_p mensalidades_faturamento_rec) AS $body$
DECLARE


	nr_seq_estrutura_mens_w		pls_regra_estrutura_bol.nr_sequencia%type;
	nr_seq_boleto_banda_aux_w	pls_boleto_banda.nr_sequencia%type;
	estrutura_bol_banda_w		pls_estrut_bol_banda%rowtype;

	C01 CURSOR FOR
		SELECT	nr_sequencia,
			ie_tipo_banda,
			qt_max_primeira_pagina,
			qt_max_reg_pagina
		from	pls_estrut_bol_banda
		where	nr_seq_estrutura	= nr_seq_estrutura_mens_w;

	
BEGIN

	if (item_mensalidade_fatumento_p.ie_tipo_pagador = 'PF') then
		nr_seq_estrutura_mens_w	:= current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_estrutura_pf_mens_w')::pls_regra_estrutura_bol.nr_sequencia%type;
	elsif (item_mensalidade_fatumento_p.ie_tipo_pagador = 'PJ') then
		nr_seq_estrutura_mens_w	:= current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_estrutura_pj_mens_w')::pls_regra_estrutura_bol.nr_sequencia%type;
	end if;

	open C01;
	loop
	fetch C01 into
		estrutura_bol_banda_w.nr_sequencia,
		estrutura_bol_banda_w.ie_tipo_banda,
		estrutura_bol_banda_w.qt_max_primeira_pagina,
		estrutura_bol_banda_w.qt_max_reg_pagina;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nextval('pls_boleto_banda_seq')
		into STRICT	nr_seq_boleto_banda_aux_w
		;

		insert into pls_boleto_banda(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_boleto,ie_tipo_banda,nr_seq_apresentacao)
		values (	nr_seq_boleto_banda_aux_w,clock_timestamp(),get_nm_usuario,clock_timestamp(),get_nm_usuario,
				nr_seq_boleto_p,estrutura_bol_banda_w.ie_tipo_banda,0);

		CALL pls_gerar_boletos_mens_fat_pck.criar_estru_boleto_bandas(estrutura_bol_banda_w,item_mensalidade_fatumento_p,nr_seq_boleto_banda_aux_w);
		end;
	end loop;
	close C01;

	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
