SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerar_boletos_mens_fat_pck.definir_estruturas_boletos () AS $body$
BEGIN

	select	max(nr_seq_estrutura)
	into STRICT	current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_estrutura_pf_mens_w')::pls_regra_estrutura_bol.nr_sequencia%type
	from	pls_regra_estrutura_bol
	where	((ie_tipo_estipulante	= 'PF') or (ie_tipo_estipulante	= 'A'));

	select	max(nr_seq_estrutura)
	into STRICT	current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_estrutura_pj_mens_w')::pls_regra_estrutura_bol.nr_sequencia%type
	from	pls_regra_estrutura_bol
	where	((ie_tipo_estipulante	= 'PJ') or (ie_tipo_estipulante	= 'A'));

	select	max(nr_seq_regra_ordem)
	into STRICT	current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_regra_ordenacao_w')::pls_regra_bol_ordem.nr_sequencia%type
	from	pls_lote_boleto
	where	nr_sequencia	= current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_lote_boleto_w')::pls_lote_boleto.nr_sequencia%type;

	select	max(nr_sequencia)
	into STRICT	current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_regra_distribuicao_w')::pls_regra_bol_distribuicao.nr_sequencia%type
	from	pls_regra_bol_distribuicao
	where	current_setting('pls_gerar_boletos_mens_fat_pck.dt_lote_boleto_w')::timestamp between coalesce(dt_inicio_vigencia,current_setting('pls_gerar_boletos_mens_fat_pck.dt_lote_boleto_w')::timestamp) and coalesce(dt_fim_vigencia,current_setting('pls_gerar_boletos_mens_fat_pck.dt_lote_boleto_w')::timestamp);

	if (current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_regra_distribuicao_w')::pls_regra_bol_distribuicao.nr_sequencia%coalesce(type::text, '') = '') then
		PERFORM set_config('pls_gerar_boletos_mens_fat_pck.nr_boleto_por_grupo_w', 0, false);
	else
		select	coalesce(nr_boleto_por_grupo,0)
		into STRICT	current_setting('pls_gerar_boletos_mens_fat_pck.nr_boleto_por_grupo_w')::bigint
		from	pls_regra_bol_distribuicao
		where	nr_sequencia	= current_setting('pls_gerar_boletos_mens_fat_pck.nr_seq_regra_distribuicao_w')::pls_regra_bol_distribuicao.nr_sequencia%type;
	end if;

	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
