CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.atualizar_analise_a700 ( nr_seq_analise_p dbms_sql.number_table ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por atualizar o tipo de origem da analise (IE_ORIGEM_ANALISE) para "Contas de A700".
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	


BEGIN

if (nr_seq_analise_p.count > 0) then
	forall i in nr_seq_analise_p.first..nr_seq_analise_p.last
		update	pls_analise_conta
		set	ie_origem_analise 	= '6' -- '6' = Contas de A700

		where	nr_sequencia		= nr_seq_analise_p(i);		

	commit;		
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
