CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.atualizar_conta_nota_cobr_a700 ( nr_seq_conta_p dbms_sql.number_table, nr_seq_nota_cobranca_p dbms_sql.number_table, vl_proc_imp_p dbms_sql.number_table, vl_mat_imp_p dbms_sql.number_table, ie_tipo_guia_p dbms_sql.varchar2_table) AS $body$
DECLARE
	
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por atualizar dados das contas e notas de cobranca.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */



BEGIN

if (nr_seq_conta_p.count > 0) then

	-- Atualiza alguns valores e o tipo de guia das contas

	forall i in nr_seq_conta_p.first..nr_seq_conta_p.last 		
		update	pls_conta
		set	vl_procedimentos_imp	= vl_proc_imp_p(i),
			vl_materiais_imp	= vl_mat_imp_p(i),
			vl_total_imp		= coalesce(vl_proc_imp_p(i), 0) + coalesce(vl_mat_imp_p(i), 0),
			ie_tipo_guia		= coalesce(ie_tipo_guia_p(i), ie_tipo_guia)
		where	nr_sequencia		= nr_seq_conta_p(i);	

	-- Atualiza a data final de geracao das contas para a data atual

	forall i in nr_seq_nota_cobranca_p.first..nr_seq_nota_cobranca_p.last 		
		update	ptu_nota_cobranca
		set	dt_fim_geracao_conta	= clock_timestamp()
		where	nr_sequencia		= nr_seq_nota_cobranca_p(i);
		
	commit;		
end if;
			
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
