CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.atualizar_conta_resumo_a700 ( nr_seq_conta_p dbms_sql.number_table ) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por:
		* Atualizar o status da conta para "A700 finalizado";
		* Eliminar/deletar o resumo da conta medica.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 			


BEGIN

if (nr_seq_conta_p.count > 0) then
	forall i in nr_seq_conta_p.first..nr_seq_conta_p.last
		update	pls_conta
		set	ie_status	= 'S' -- 'S' = A700 finalizado

		where	nr_sequencia	= nr_seq_conta_p(i);
		
	forall i in nr_seq_conta_p.first..nr_seq_conta_p.last	
		delete 	FROM pls_conta_medica_resumo
		where	nr_seq_conta 	= nr_seq_conta_p(i);

	commit;		
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_contas_a700_pck.atualizar_conta_resumo_a700 ( nr_seq_conta_p dbms_sql.number_table ) FROM PUBLIC;
