CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.definir_conta_resumo_a700 ( nr_seq_protocolo_p pls_conta.nr_seq_protocolo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por:
		* Atualizar o status da conta para "A700 finalizado";
		* Eliminar/deletar o resumo da conta medica.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 			

i			integer;
nr_seq_conta_table_w	dbms_sql.number_table;	

C01 CURSOR(	nr_seq_protocolo_pc	pls_conta.nr_seq_protocolo%type) FOR
	SELECT	nr_sequencia nr_seq_conta
	from	pls_conta
	where	nr_seq_protocolo 	= nr_seq_protocolo_pc
	and	(nr_seq_analise IS NOT NULL AND nr_seq_analise::text <> '');	
			
BEGIN

i := 1;
	
-- Cursor para filtrar as contas que possuam uma analise vinculada

for r_c01_w in C01(nr_seq_protocolo_p) loop
	begin		
	nr_seq_conta_table_w(i)	:= r_c01_w.nr_seq_conta;
	
	if (i >= current_setting('pls_gerar_contas_a700_pck.qt_registro_transacao_w')::integer) then
		-- Atualiza o status da conta para "A700 finalizado" / Elimina o resumo da conta medica

		CALL pls_gerar_contas_a700_pck.atualizar_conta_resumo_a700(nr_seq_conta_table_w);
		
		-- Limpa a variavel table

		nr_seq_conta_table_w.delete;
		
		i := 1;			
	else
		i := i + 1;
	end if;			
	end;
end loop;

-- Caso sobre algum item dentro das variaveis table, estes tambem precisam ser atualizadas

CALL pls_gerar_contas_a700_pck.atualizar_conta_resumo_a700(nr_seq_conta_table_w);	

-- Limpa a variavel table

nr_seq_conta_table_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_contas_a700_pck.definir_conta_resumo_a700 ( nr_seq_protocolo_p pls_conta.nr_seq_protocolo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) FROM PUBLIC;
