CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.fechar_analise_conta_a700 ( nr_seq_protocolo_p pls_conta.nr_seq_protocolo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE
	
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por:
		* Fechar a analise e gerar a nota de pos caso o usuario possua permissao;
		* Atualizar o tipo de origem da analise (IE_ORIGEM_ANALISE) para "Contas de A700".
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 				

i			integer;
nr_seq_analise_table_w	dbms_sql.number_table;
qt_contas_abertas	integer := 0;

C01 CURSOR(	nr_seq_protocolo_pc	pls_conta.nr_seq_protocolo%type) FOR
	SELECT	distinct nr_seq_analise
	from	pls_conta
	where	nr_seq_protocolo 	= nr_seq_protocolo_pc
	and	(nr_seq_analise IS NOT NULL AND nr_seq_analise::text <> '');
			
BEGIN

i := 1;
	
-- Cursor para filtrar o NR_SEQ_ANALISE das contas

for r_c01_w in C01(nr_seq_protocolo_p) loop
	begin		
	-- Fecha a analise e gera a nota de pos caso o usuario possua permissao

	
	select count(1)
	into STRICT	qt_contas_abertas
	from	pls_conta
	where	nr_seq_analise = r_c01_w.nr_seq_analise
	and		ie_status != 'F';
	
	if ( qt_contas_abertas > 0) then	 		
		CALL pls_analise_fechar_contas(r_c01_w.nr_seq_analise, null, cd_estabelecimento_p, nm_usuario_p, 'S');
	end if;
	
	nr_seq_analise_table_w(i) := r_c01_w.nr_seq_analise;
	
	if (i >= current_setting('pls_gerar_contas_a700_pck.qt_registro_transacao_w')::integer) then
		-- Atualiza o tipo de origem da analise (IE_ORIGEM_ANALISE) para "Contas de A700"

		CALL pls_gerar_contas_a700_pck.atualizar_analise_a700(nr_seq_analise_table_w);
			
		-- Limpa a variavel table

		nr_seq_analise_table_w.delete;
		
		i := 1;
	else
		i := i + 1;
	end if;			
	end;
end loop;	

-- Caso sobre algum item dentro das variaveis table, estes tambem precisam ser atualizadas

CALL pls_gerar_contas_a700_pck.atualizar_analise_a700(nr_seq_analise_table_w);

-- Limpa a variavel table

nr_seq_analise_table_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_contas_a700_pck.fechar_analise_conta_a700 ( nr_seq_protocolo_p pls_conta.nr_seq_protocolo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) FROM PUBLIC;
