CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.inserir_conta_mat_a700 ( dados_mat_p pls_gerar_contas_a700_pck.dados_mat, nr_seq_mat_table_p INOUT dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por gerar os materiais.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	


BEGIN
			
if (dados_mat_p.ie_tipo_despesa.count > 0) then
	forall i in dados_mat_p.ie_tipo_despesa.first..dados_mat_p.ie_tipo_despesa.last
		insert into pls_conta_mat(	nr_sequencia, 					nm_usuario, 					dt_atualizacao,
			nm_usuario_nrec, 				dt_atualizacao_nrec, 				nr_seq_material,
			dt_atendimento,					tx_reducao_acrescimo, 				qt_material, 
			vl_material_imp, 				ie_status, 					nr_seq_conta,
			ie_situacao, 					vl_unitario_imp, 				qt_material_imp,
			ie_tipo_despesa, 				vl_taxa_material_imp, 				cd_material_imp,
			dt_inicio_atend,				ds_material_imp, 				ie_ato_cooperado,
			tx_intercambio_imp, 				ie_pacote_ptu, 					vl_liberado,
			vl_unitario )
		values (	nextval('pls_conta_mat_seq'),			nm_usuario_p, 					clock_timestamp(),
			nm_usuario_p, 					clock_timestamp(), 					dados_mat_p.nr_seq_material(i),
			dados_mat_p.dt_atendimento(i),			dados_mat_p.tx_reducao_acrescimo(i), 		dados_mat_p.qt_material(i), 
			dados_mat_p.vl_material_imp(i), 		dados_mat_p.ie_status(i), 			dados_mat_p.nr_seq_conta(i),
			dados_mat_p.ie_situacao(i), 			dados_mat_p.vl_unitario_imp(i), 		dados_mat_p.qt_material_imp(i),
			dados_mat_p.ie_tipo_despesa(i), 		dados_mat_p.vl_taxa_material_imp(i), 		dados_mat_p.cd_material_imp(i),
			dados_mat_p.dt_inicio_atend(i),			dados_mat_p.ds_material_imp(i), 		dados_mat_p.ie_ato_cooperado(i),
			dados_mat_p.tx_intercambio_imp(i), 		dados_mat_p.ie_pacote_ptu(i), 			dados_mat_p.vl_liberado(i),
			dados_mat_p.vl_unitario(i))	
			returning nr_sequencia bulk collect into nr_seq_mat_table_p;	
			
	commit;		
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
