CREATE OR REPLACE PROCEDURE pls_gerar_contas_a700_pck.inserir_proc_particip_a700 ( nr_seq_conta_proc_p dbms_sql.number_table, ie_tipo_particip_p dbms_sql.varchar2_table, nm_medico_exec_p dbms_sql.varchar2_table, vl_apresentado_p dbms_sql.number_table, nr_seq_partic_table_p INOUT dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por gerar os participantes dos procedimentos.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	
			

BEGIN
			
if (nr_seq_conta_proc_p.count > 0) then
	forall i in nr_seq_conta_proc_p.first..nr_seq_conta_proc_p.last
		insert into pls_proc_participante(	nr_sequencia, 					dt_atualizacao, 				nm_usuario,
			dt_atualizacao_nrec, 				nm_usuario_nrec, 				nr_seq_conta_proc, 
			nm_medico_executor_imp,				vl_apresentado,					ie_status,
			nr_seq_grau_partic )
		values(	nextval('pls_proc_participante_seq'), 		clock_timestamp(), 					nm_usuario_p,
			clock_timestamp(), 					nm_usuario_p, 					nr_seq_conta_proc_p(i), 
			nm_medico_exec_p(i),				vl_apresentado_p(i),				'L',
			(select	max(x.nr_sequencia) from pls_grau_participacao x where x.cd_ptu = ie_tipo_particip_p(i)))
		returning nr_sequencia bulk collect into STRICT nr_seq_partic_table_p;	
		
	commit;	
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
