SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerar_lote_pre_pagto_pck.desfazer_geracao_dados ( nr_seq_lote_p pls_lote_serv_pre_pgto.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE
 							

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_faturamento_prot a
	where	a.nr_seq_lote_serv_pre_pgto = nr_seq_lote_p;	

C02 CURSOR( nr_seq_prot_pc	pls_faturamento_conta.nr_seq_protocolo%type) FOR
	SELECT	nr_sequencia
	from	pls_faturamento_conta a
	where	a.nr_seq_protocolo = nr_seq_prot_pc;

C03 CURSOR(	nr_seq_conta_pc	pls_faturamento_conta.nr_sequencia%type ) FOR
	SELECT	nr_sequencia
	from	pls_fat_conta_item a
	where	a.nr_seq_conta = nr_seq_conta_pc;	
	
C04 CURSOR(	nr_seq_conta_item_pc	pls_fat_conta_item.nr_sequencia%type ) FOR
	SELECT	'P' ie_tipo,
			a.nr_sequencia
	from	pls_fat_conta_proc a
	where	a.nr_seq_item_conta = nr_seq_conta_item_pc
	
union all

	SELECT	'M' ie_tipo,
			a.nr_sequencia
	from	pls_fat_conta_mat a
	where	a.nr_seq_item_conta = nr_seq_conta_item_pc;
	
	
									
BEGIN

	--Protocolos

	for r_c01_w in C01 loop
	
		--Contas

		for r_c02_w in C02(r_c01_w.nr_sequencia) loop
		
			--Conta_item

			for r_c03_w in C03(r_c02_w.nr_sequencia) loop
			
				--Proc e mat

				for r_c04_w in C04(r_c03_w.nr_sequencia) loop			
					if (r_c04_w.ie_tipo = 'P') then
					
						--excluir participantes

						delete from pls_fat_conta_item_equipe where nr_seq_item_conta = r_c03_w.nr_sequencia and nr_seq_conta_proc = r_c04_w.nr_sequencia;
					
						delete from pls_fat_conta_proc where nr_seq_item_conta = r_c03_w.nr_sequencia;
						
					else					
						delete from pls_fat_conta_mat where nr_seq_item_conta = r_c03_w.nr_sequencia;
					
					end if;
				
				end loop;
			
				delete from pls_fat_conta_item where nr_sequencia = r_c03_w.nr_sequencia;
			
			end loop;
			
			delete from pls_faturamento_inc where nr_seq_conta = r_c02_w.nr_sequencia;
			delete from pls_fat_decl_conta_obito where nr_seq_conta = r_c02_w.nr_sequencia;
			delete from pls_fat_decl_conta_vivo where nr_seq_conta = r_c02_w.nr_sequencia;
			delete from pls_fat_diagnostico_conta where nr_seq_conta = r_c02_w.nr_sequencia;
			delete from pls_faturamento_conta where nr_sequencia = r_c02_w.nr_sequencia;
			
		end loop;
		
		delete from pls_faturamento_prot
		where 	nr_sequencia = r_c01_w.nr_sequencia 
		and 	nr_seq_lote_serv_pre_pgto = nr_seq_lote_p;	
		commit;
	
	end loop;
	
	update	pls_lote_serv_pre_pgto
	set		dt_geracao_lote		 = NULL
	where	nr_sequencia		= nr_seq_lote_p;
	commit;
							
END;									

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_lote_pre_pagto_pck.desfazer_geracao_dados ( nr_seq_lote_p pls_lote_serv_pre_pgto.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
