	--Gera informacoes adicionais de contas(diagnosticos, e declaracoes) que posteriormente serao exportadas para arquivo(tabelas pls_faturamento_prot e pls_faturamento_conta).

	CALL pls_gerar_lote_pre_pagto_pck.gerar_info_adicionais_conta(nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);
	
