	--Obs: QUando ficar definido como ficara a questao da sequencia interna, necessario rever e alimentar aqui isso.

	CALL CALL pls_gerar_lote_pre_pagto_pck.gerar_materiais(nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);
	
