SET client_encoding TO 'UTF8';


-- Rotina resonsável por atualizar o status dos lotes de franquia e A800 caso o arquivo seja rejeitado
CREATE OR REPLACE PROCEDURE pls_gerar_pre_estab_ans_pck.atualiza_status_arq_rej ( nr_seq_lote_ret_p pls_monitor_tiss_lote_ret.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_arquivo_w	pls_monitor_tiss_arquivo.nr_sequencia%type;
nr_seq_lote_com_w	pls_monitor_tiss_lote_com.nr_sequencia%type;
qt_contratacoes_w	integer;
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_arquivo_pc	pls_monitor_tiss_arquivo.nr_sequencia%type) FOR
	SELECT	nr_identificador
	from	pls_monit_tiss_pre_est
	where	nr_seq_arq_monitor = nr_seq_arquivo_pc
	and	(nr_seq_prestador IS NOT NULL AND nr_seq_prestador::text <> '');

C02 CURSOR(	dt_inicio_pc		pls_monitor_tiss_lote.dt_mes_competencia%type,
		dt_fim_pc		pls_monitor_tiss_lote.dt_mes_competencia%type,
		nr_seq_regra_pc		pls_regra_franq_pag.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia
	from	pls_franq_pag a,
		pls_franq_pag_prest b
	where	a.nr_sequencia = b.nr_seq_franq_pag
	and	a.dt_referencia between dt_inicio_pc and dt_fim_pc
	and	b.nr_seq_regra = nr_seq_regra_pc
	and	b.ie_status_monitoramento = 'PR';

C03 CURSOR(nr_seq_arquivo_pc	pls_monitor_tiss_arquivo.nr_sequencia%type) FOR
	SELECT	nr_identificador
	from	pls_monit_tiss_pre_est
	where	nr_seq_arq_monitor = nr_seq_arquivo_pc
	and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');

BEGIN

select	max(nr_seq_arquivo),
	max(nr_seq_lote_com)
into STRICT	nr_seq_arquivo_w,
	nr_seq_lote_com_w
from	pls_monitor_tiss_lote_ret
where	nr_sequencia = nr_seq_lote_ret_p;

-- Atualiza todos os registros dentro do arquivo
for r_C01_w in C01(nr_seq_arquivo_w) loop

	Open C02(current_setting('pls_gerar_pre_estab_ans_pck.dt_inicio_ref_w')::pls_monitor_tiss_lote.dt_mes_competencia%type, current_setting('pls_gerar_pre_estab_ans_pck.dt_fim_ref_w')::pls_monitor_tiss_lote.dt_mes_competencia%type, r_C01_w.nr_identificador);
	loop
		tb_nr_sequencia_w.delete;

		fetch C02 bulk collect into tb_nr_sequencia_w
		limit current_setting('pls_gerar_pre_estab_ans_pck.qt_transacao_w')::integer;

		exit when tb_nr_sequencia_w.count = 0;

		forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
			update	pls_franq_pag_prest
			set	ie_status_monitoramento = 'N'
			where	nr_sequencia = tb_nr_sequencia_w(i);
		commit;
	end loop;

	if (C02%ISOPEN) then
		close C02;
	end if;
end loop;

Open C03(nr_seq_arquivo_w);
loop
	tb_nr_sequencia_w.delete;

	fetch C03 bulk collect into tb_nr_sequencia_w
	limit current_setting('pls_gerar_pre_estab_ans_pck.qt_transacao_w')::integer;

	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		update	ptu_fatura_pre
		set	ie_status_monitoramento = 'N'
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;

-- Atualiza os totalizadores com a quantidade de contratações enviadas
select	count(1)
into STRICT	qt_contratacoes_w
from	pls_monit_tiss_pre_est
where	nr_seq_arq_monitor = nr_seq_arquivo_w;

update	pls_monitor_tiss_lote_ret
set	qt_total_incluido	= 0,
	qt_total_alterado	= 0,
	qt_total_excluido	= 0,
	qt_total_erro		= qt_contratacoes_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_lote_ret_p;

CALL pls_gerar_pre_estab_ans_pck.atualiza_dados_ret_com(nr_seq_lote_com_w, nm_usuario_p);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_pre_estab_ans_pck.atualiza_status_arq_rej ( nr_seq_lote_ret_p pls_monitor_tiss_lote_ret.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
