-- Rotina responsável por atualizar o status do lote de monitoramento
CREATE OR REPLACE PROCEDURE pls_gerar_pre_estab_ans_pck.atualiza_status_lote (nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_inconsistencia_w		integer;


BEGIN
-- Busca quantas inconsistências foram lançadas
select	count(1)
into STRICT	qt_inconsistencia_w
from	pls_monitor_tiss_inc_pre a,
	pls_monitor_tiss_inc b
where	a.cd_inconsistencia = b.cd_inconsistencia
and	a.nr_seq_pre_estab_val in (	SELECT	x.nr_sequencia
					from	pls_monit_tiss_pre_est_val x
					where	x.nr_seq_lote_monitor = nr_seq_lote_p);

-- Caso possua alguma inconsistência, não libera o lote
if (qt_inconsistencia_w > 0) then
	update 	pls_monitor_tiss_lote
	set	ie_status = 'PI',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_lote_p;
else
	update 	pls_monitor_tiss_lote
	set	ie_status = 'LE',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_lote_p;
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
