-- Atualiza os totalizadores do lote do monitoramento
CREATE OR REPLACE PROCEDURE pls_gerar_pre_estab_ans_pck.atualiza_totalizadores_lote ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registros_w		integer;
vl_total_franquia_w	pls_monit_tiss_pre_est_val.vl_total_franquia%type;


BEGIN

select	count(1),
	coalesce(sum(vl_total_franquia),0)
into STRICT	qt_registros_w,
	vl_total_franquia_w
from	pls_monit_tiss_pre_est_val
where	nr_seq_lote_monitor = nr_seq_lote_p;

update	pls_monitor_tiss_lote
set	qt_franquia 	= qt_registros_w,
	vl_total_pago 	= vl_total_franquia_w,
	nm_usuario 	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia 	= nr_seq_lote_p;

commit;

END;

$body$
