-- Rotina chamada dentro do Delphi, responsável por desfazer os dados gerados
CREATE OR REPLACE PROCEDURE pls_gerar_pre_estab_ans_pck.desfazer_dados_gerados ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_monit_tiss_pre_est_val
	where	nr_seq_lote_monitor = nr_seq_lote_pc;


BEGIN
-- grava o log de processo que sinaliza a ação que foi feita
CALL pls_gerar_pre_estab_ans_pck.grava_processo(nr_seq_lote_p, 6, nm_usuario_p);

-- Deleta tudo que foi gerado
Open C01(nr_seq_lote_p);
loop
	tb_nr_sequencia_w.delete;

	fetch C01 bulk collect into tb_nr_sequencia_w
	limit current_setting('pls_gerar_pre_estab_ans_pck.qt_transacao_w')::integer;

	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		delete	FROM pls_monitor_tiss_inc_pre
		where	nr_seq_pre_estab_val = tb_nr_sequencia_w(i);
	commit;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		delete	FROM pls_monit_tiss_pre_est_val
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;

if (C01%ISOPEN) then
	close C01;
end if;

-- Atualiza os totalizadores
CALL pls_gerar_pre_estab_ans_pck.atualiza_totalizadores_lote(nr_seq_lote_p, nm_usuario_p);

-- Atualiza status do lote
update	pls_monitor_tiss_lote
set	ie_status 	= 'U',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario 	= nm_usuario_p
where	nr_sequencia 	= nr_seq_lote_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
